template:
@keyframes cardShake {
  0%, 100% { transform: rotate(0); }
  25% { transform: rotate({{shakeDegree}}deg); }
  50% { transform: rotate(-{{shakeDegree}}deg); }
  75% { transform: rotate({{shakeDegree/4}}deg); }
  85% { transform: rotate(-{{shakeDegree/4}}deg); }
}

.item-card:hover {
  {{#if enableScale}}
  transform: scale({{scaleSize}});
  animation: cardShake {{shakeSpeed}}s {{scaleDelay}}s ease-in-out forwards;
  {{else}}
  animation: cardShake {{shakeSpeed}}s ease-in-out forwards;
  {{/if}}
}
