template:
/* 装饰效果样式 */
.app-icon-info-text-box-title {
  position: relative;
}

.app-icon-info-text-box-title:before {
  content: "";
  position: absolute;
  width: {{beforeCircleSize}}px;
  height: {{beforeCircleSize}}px;
  border-radius: 60%;
  background: {{beforeCircleColor}};
  box-shadow: -8px 21px 0 {{beforeCircleShadowColor}};
  z-index: -1;
  right: {{beforeCircleRight}}px;
  top: {{beforeCircleTop}}px;
  pointer-events: none;
}

.app-icon-info-text-box-title:after {
  content: "";
  position: absolute;
  width: {{afterCircleSize}}px;
  height: {{afterCircleSize}}px;
  border: {{afterCircleBorderWidth}}px solid {{afterCircleColor}};
  border-radius: 70%;
  z-index: -1;
  top: {{afterCircleTop}}px;
  right: {{afterCircleRight}}px;
  pointer-events: none;
}

.item-card {
  background: {{cardBackground}};
  opacity: {{cardOpacity}};
  -webkit-backdrop-filter: blur({{blurAmount}}px);
  backdrop-filter: blur({{blurAmount}}px);
}
