template:
/* 背景线条样式 */
.w-full .font-semibold:before {
  position: absolute;
  width: 93px;
  display: block;
  height: 75px;
  content: "";
  border-radius: 60%;
  z-index: -1;
  right: -27px;
  top: -35px;
  background: {{beforeCircleColor}};
  box-shadow: -8px 21px 0 {{beforeCircleShadowColor}};
}

.w-full .font-semibold:after {
  position: absolute;
  width: 40px;
  display: block;
  height: 40px;
  border: 4px solid {{afterCircleColor}};
  content: "";
  border-radius: 70%;
  top: -19px;
  right: 48px;
  z-index: -1;
}

.icon-info-box .rounded-2xl {
  position: relative;
  border-radius: 15px;
  overflow: hidden;
  -webkit-backdrop-filter: blur({{blurAmount}}px);
  backdrop-filter: blur({{blurAmount}}px);
}
